import { useEffect, useReducer, useState } from "react"
import { useSelector } from "react-redux"
import mode from "../../services/indexedDB"
import {
  PlaybtnSvg,
  NextBtnSvg,
  PrevBtnSvg,
  AddbookmarkSvg,
  Controls,
  ControlsBtn,
  MarkControlsBtnright,
  UnMarkControlsBtnright,
  VolumeSvg,
  VolumeWrapper,
  StatusSpace

} from "./Controller.styles"


const initAudio =
{
  success: false,
  pending: false,
  error: false,
}


const reducer = (state, action) => {
  console.log(action)
  switch (action.type) {
    case "start":
      console.log("start")
      return { success: false, pending: true, error: false }
    case "success":
      console.log('sucess')
      return { success: true, pending: false, error: false }
    case "fail":
      console.log("fail")
      return { success: false, pending: false, error: true }
  }
}


function startPlayback(handleStart) {
  handleStart()

  return document.getElementById('player').play()
}


export let useController = () => {
  const [currentAudioStatus, dispatch] = useReducer(reducer, initAudio);
  const [volume, setVolume] = useState(false)
  const handleStart = () => {
    dispatch({ type: "start" });
  };
  const handlesuccess = () => {
    dispatch({ type: "success" });
  };
  const handleError = () => {
    dispatch({ type: "fail" });
  };

  const uuid = useSelector(state => state.radio.audio.uuid)
  const trackName = useSelector(state => state.radio.audio.trackName)
  const [playBtn, setPlayBtn] = useState(false)
  useEffect(() => {
    if (trackName == undefined) return
    startPlayback(handleStart).then(() => {
      handlesuccess()
      setPlayBtn(true)
      console.log("play")
    }).catch(() => {
      handleError()
      console.log("error")
    })
  }, [trackName])

  let playTrigger = (e) => {
    if (trackName == undefined) { alert("plz select song first"); return }
    console.log("cl")
    let t = document.getElementById('player')
    //  play
    if (t.currentTime > 0 && !t.paused && !t.ended) {
      t.pause()
      setPlayBtn(false)
    } else {
      setPlayBtn(true);
      t.play().then(
      ).catch((e) => {
        console.error(e)
      })
    }
  }
  let prevTrigger = (e) => {
    let currentDataId = document.querySelector(`div[data-id='${uuid}']`)
    currentDataId.previousSibling.click()
  }
  let nextTrigger = (e) => {
    let currentDataId = document.querySelector(`div[data-id='${uuid}']`)
    currentDataId.nextSibling.click()
  }
  let volumeMuteTrigger = () => {
    let muteBtn = document.getElementById('player')
    if (muteBtn.muted) {
      //pause true
      muteBtn.muted = false
      setVolume(false)
    } else {
      muteBtn.muted = true
      setVolume(true)
    }

  }


  return { volume, volumeMuteTrigger, playTrigger, playBtn, prevTrigger, nextTrigger, currentAudioStatus }


}

const Controller = () => {

  const name = useSelector(state => state.radio.audio.name)
  const trackName = useSelector(state => state.radio.audio.trackName)
  const baseUrl = useSelector(state => state.radio.audio.baseUrl)
  const coverPic = useSelector(state => state.radio.audio.converPic)
  const currentplayingUrl = useSelector(state => state.radio.audio.currentplayingUrl)
  const tag = useSelector(state => state.radio.audio.tag)
  const color = useSelector(state => state.radio.audio.color)
  const [bookmark, setBookmark] = useState(true)
  const uuid = useSelector(state => state.radio.audio.uuid)
  const { playTrigger, playBtn, prevTrigger, nextTrigger, volumeMuteTrigger, volume } = useController()

  useEffect(() => {
    //indexedDB view is data available
    if (uuid == undefined) return
    mode().then((store) => {
      store.dataViewdb(uuid).onsuccess = (event) => {
        event.target.result === undefined ? setBookmark(true) : setBookmark(false)
      }
    })

  }, [trackName])
  let putData = () => {
    return {
      id: uuid.toString(),
      trackName: trackName.toString(),
      baseUrl: baseUrl,
      coverPic: coverPic,
      currentplayingUrl: currentplayingUrl,
      tag: tag,
      color: color,
      uuid: uuid
    }
  }
  let addDB = (store) => {
    mode().then((store) => {
      store.dataPutdb(putData())
    })

    setBookmark(false)
  }
  let removeDB = async () => {
    mode().then((store) => {
      store.dataDeletedb(uuid);
    })
    setBookmark(true)
  }
  return (
    <>
      <Controls>
        <VolumeWrapper onClick={volumeMuteTrigger} >
          <VolumeSvg mutevolume={volume ? 1 : 0} />
        </VolumeWrapper>

        <ControlsBtn>
          <PrevBtnSvg onClick={prevTrigger}>
          </PrevBtnSvg>
        </ControlsBtn>

        <ControlsBtn>

          <PlaybtnSvg playbtnstate={playBtn ? 1 : 0} onClick={playTrigger} >
          </PlaybtnSvg>

        </ControlsBtn>

        <ControlsBtn onClick={nextTrigger}>
          <NextBtnSvg>
          </NextBtnSvg>
        </ControlsBtn>

        {
          bookmark ?
            < MarkControlsBtnright onClick={addDB}
            >
              <AddbookmarkSvg />

            </MarkControlsBtnright>
            :
            < UnMarkControlsBtnright onClick={removeDB}
            >
              <AddbookmarkSvg />

            </UnMarkControlsBtnright>}
      </Controls>
    </>
  )
}


export default Controller
